import { Routes } from '@angular/router';
import { HomePageComponent } from './pages/home/home.component';
import { ProductListPageComponent } from './pages/product-list/product-list.component';
import { ProductDetailsPageComponent } from './pages/product-details/product-details.component';
import { LoginPageComponent } from './pages/login/login.component';
import { RegisterPageComponent } from './pages/register/register.component';
import { ShoppingCartComponent } from './pages/shopping-cart/shopping-cart.component';
import { CheckoutPageComponent } from './pages/checkout/checkout.component';

// 1. IMPORT THE NEW COMPONENTS
import { authGuard } from './guards/auth.guard';
import { OrderConfirmationPageComponent } from './pages/order-confirmation/order-confirmation.component';

export const appRoutes: Routes = [
    // --- Public Routes ---
    { path: '', component: HomePageComponent },
    { path: 'kits', component: ProductListPageComponent },
    { path: 'kit/:slug', component: ProductDetailsPageComponent },
    { path: 'login', component: LoginPageComponent },
    { path: 'register', component: RegisterPageComponent },

    // --- Protected Routes (require login) ---
    { 
        path: 'cart', 
        component: ShoppingCartComponent,
        canActivate: [authGuard] // This guard protects the route
    },
    { 
        path: 'checkout', 
        component: CheckoutPageComponent,
        canActivate: [authGuard] // This guard protects the route
    },
    // 2. ADD THE NEW ROUTE FOR THE THANK YOU PAGE
    // It should also be protected so only logged-in users who just ordered can see it.
    {
        path: 'order-confirmation/:id',
        component: OrderConfirmationPageComponent,
        canActivate: [authGuard]
    },

    // --- Admin Module ---
    {
        path: 'admin',
        loadChildren: () => import('./admin/admin.routes').then(m => m.ADMIN_ROUTES)
    },
    
    // --- Wildcard Route ---
    { path: '**', redirectTo: '' }
];
